mapper.delete:
DELETE FROM COMMENTS
WHERE commentNo = #{commentNo}
AND fk_id = #{fk_id}

mapper.update:
UPDATE COMMENTS
SET content = #{content},
    updatedAtComment = SYSDATE
WHERE commentNo = #{commentNo}
AND fk_id = #{fk_id}

mapper.select:
SELECT fk_id
FROM COMMENTS
WHERE commentNo = #{commentNo}
